import React from 'react';
import PropType from 'prop-types';
import { Field } from 'formik';
import { Combobox } from 'react-widgets';

import { FormGroup } from "ui/control";

export const ComboboxField = ({name, title, autoFocus, data}) => (
    <FormGroup title={title} name={name}>
        {/* <label> is not functional, because Combobox doesn't allow to set id */}
        <Field name={name}>
            {({field, form}) => {
                const touched = form.touched[field.name];
                const error = form.errors[field.name];
                return (
                    <>
                        <Combobox
                            name={field.name}
                            value={field.value}
                            onChange={v => form.setFieldValue(field.name, v)}
                            suggest
                            autoFocus={autoFocus}
                            data={data}
                        />
                        {touched && error && <div className="invalid-feedback">{error}</div>}
                    </>
                );
            }}
        </Field>
    </FormGroup>
);

ComboboxField.propTypes = {
    name: PropType.string,
    title: PropType.string,
    autoFocus: PropType.bool,
    data: PropType.array
};
