import React from 'react';
import PropType from 'prop-types';
import cx from 'classnames';

export const FormGroup = ({title, name, horizontal = false, groupClassName, labelClassName, children}) => (
    <div className={cx(
            "form-group",
            groupClassName, {
                "row": horizontal
            })}>
        {title && <label htmlFor={name} className={cx(
            labelClassName, {
            "col-form-label": horizontal
        })}>{title}</label>}
        {children}
    </div>
);

FormGroup.propTypes = {
    title: PropType.string,
    name: PropType.string,
    horizontal: PropType.bool,
    groupClassName: PropType.string,
    labelClassName: PropType.string,
    children: PropType.element
};
