import React from 'react';
import { connect } from 'react-redux';

import ConnectionStatus from "ui/logobar/connectionstatus/ConnectionStatus";
import { goToPage } from "state/navigation/actions";
import { PAGE_INDEX } from "state/navigation/pages";
import "./LogoBar.css";

const LogoBar = ({goToPage}) => (
    <div id="logo-bar">
        <div id="logo-bar-left">
            <a href="/" onClick={event => {
                goToPage(PAGE_INDEX);
                event.preventDefault();
            }}>
                <img id="logo-image" src="/pics/logo-w-64.png" alt="Moera" />
            </a>
        </div>
        <div id="logo-bar-right">
            <ConnectionStatus />
        </div>
    </div>
);

export default connect(
    null,
    { goToPage }
)(LogoBar);
