import React from 'react';
import PropType from 'prop-types';
import { connect } from 'react-redux';

const MainMenuLink = ({rootLocation, href, children}) => (
    <li className="nav-item"><a className="nav-link" href={rootLocation + href}>{children}</a></li>
);

MainMenuLink.propTypes = {
    href: PropType.string.isRequired
};

export default connect(
    (state) => ({rootLocation: state.node.root.location})
)(MainMenuLink);
