import React from 'react';
import PropType from 'prop-types';
import { connect } from 'react-redux';
import cx from 'classnames';

const MainMenuLink = ({rootLocation, currentPage, page, href, children}) => (
    <li className={cx(["nav-item", {"active": currentPage === page}])}>
        <a className="nav-link" href={rootLocation + href}>{children}</a>
    </li>
);

MainMenuLink.propTypes = {
    href: PropType.string.isRequired
};

export default connect(
    (state) => ({
        rootLocation: state.node.root.location,
        currentPage: state.navigation.page
    })
)(MainMenuLink);
