import React from 'react';
import { connect } from 'react-redux';

import "./HomeName.css";

const HomeName = ({name}) => (
    name ?
        <span className="navbar-text home-name">{name}</span>
    :
        <span className="navbar-text home-name anonymous">no name set</span>
);

export default connect(
    state => ({
        name: state.home.owner.name
    })
)(HomeName);
