import React from 'react';
import { connect } from 'react-redux';

import { goToTimeline } from "state/navigation/actions";
import "./Logo.css";

const Logo = () => (
    <div id="logo" className="navbar-brand">
        <a href="/moera" onClick={event => {
            goToTimeline();
            event.preventDefault();
        }}>
            <img id="logo-image" src="/pics/logo-w-32.png" alt="Moera" />
        </a>
    </div>
);

export default connect(
    null,
    {goToTimeline}
)(Logo);
