import React from 'react';
import { connect } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import cx from 'classnames';

import { REACTION_EMOJIS } from "api";
import { Twemoji } from "ui/control";
import { postingReact, postingReactionDelete } from "state/postings/actions";
import "./PostingButton.css";

const PostingButton = ({id, icon, caption, invisible, negative, emoji, postingReact, postingReactionDelete}) => {
    if (emoji == null) {
        return (
            <div className={cx("posting-button", {"invisible": invisible})} onClick={
                e => postingReact(id, negative, negative ? 0x1f44e : 0x1f44d)
            }>
                <FontAwesomeIcon icon={["far", icon]}/>
                <span className="caption">{caption}</span>
            </div>
        );
    } else {
        const re = !negative ? REACTION_EMOJIS.positive[emoji] : REACTION_EMOJIS.negative[emoji];
        return (
            <div className="posting-button" onClick={
                e => postingReactionDelete(id)
            }>
                <Twemoji code={emoji}/>
                {re ?
                    <span className="caption" style={{color: re.color}}>{re.title}</span>
                    :
                    <span className="caption">{caption}</span>}
            </div>
        );
    }
};

export default connect(
    null,
    { postingReact, postingReactionDelete }
)(PostingButton);
