import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import { REACTION_EMOJIS } from "api";
import { Twemoji } from "ui/control";
import "./PostingButtons.css";

const PostingButton = ({icon, caption, negative, emoji}) => {
    if (emoji == null) {
        return (
            <div className="posting-button">
                <FontAwesomeIcon icon={["far", icon]}/>
                <span className="caption">{caption}</span>
            </div>
        );
    } else {
        const re = !negative ? REACTION_EMOJIS.positive[emoji] : REACTION_EMOJIS.negative[emoji];
        console.log(negative, emoji, re);
        return (
            <div className="posting-button">
                <Twemoji code={emoji}/>
                {re ?
                    <span className="caption" style={{color: re.color}}>{re.title}</span>
                :
                    <span className="caption">{caption}</span>}
            </div>
        );
    }
};

const PostingButtons = ({posting}) => {
    const cr = posting.clientReaction;
    return (
        <div className="posting-buttons">
            <PostingButton icon="thumbs-up" caption="Support" negative={false}
                           emoji={cr && !cr.negative ? cr.emoji : null}/>
            <PostingButton icon="thumbs-down" caption="Oppose" negative={true}
                           emoji={cr && cr.negative ? cr.emoji : null}/>
        </div>
    );
};

export default PostingButtons;
