import React from 'react';

import "./PostingSubject.css";

const PostingSubject = ({posting, preview}) => (
    preview && posting.bodyPreview.subject ?
        <div className="subject">{posting.bodyPreview.subject}</div>
    :
        (posting.body.subject ? <div className="subject">{posting.body.subject}</div> : null)
);

export default PostingSubject;
