import React from 'react';
import moment from 'moment';

const PostingUpdated = ({posting}) => (
    posting.totalRevisions > 1 ? (
        <span className="date">
            {" "}(updated {moment.unix(posting.editedAt).format("DD-MM-YYYY HH:mm")})
        </span>
    ) : null
);

export default PostingUpdated;
