import React from 'react';
import { connect as connectFormik } from 'formik';

import { Button } from "ui/control";
import "./SettingsButtons.css";

const SettingsButtons = ({formik}) => {
    const {dirty, handleReset} = formik;

    return (
        <div className="settings-buttons">
            <Button variant="secondary" className="col-2" disabled={!dirty} onClick={handleReset}>
                Cancel
            </Button>
            <Button variant="primary" type="submit" className="col-2" disabled={!dirty}>Save</Button>
        </div>
    );
};

export default connectFormik(SettingsButtons);
