import React from 'react';

import { Page } from "ui/page/Page";
import SettingsTabs from "ui/settings/SettingsTabs";
import SettingsMenu from "ui/settings/SettingsMenu";
import "./SettingsPage.css";
import { connect } from "react-redux";

const SettingsPage = ({nodeValues}) => (
    <Page>
        <h2>Settings</h2>
        <br/>

        <SettingsTabs />
        <div className="row settings-notebook">
            <SettingsMenu />
            <div className="col-md-10">
                {nodeValues.map(({name, value}) => <div key={name}><b>{name}</b>: {value}</div>)}
            </div>
        </div>
    </Page>
);

export default connect(
    state => ({
        nodeValues: state.settings.node.values
    })
)(SettingsPage);
