import React from 'react';
import { connect } from 'react-redux';

import { Page } from "ui/page/Page";
import SettingsTabs from "ui/settings/SettingsTabs";
import SettingsMenu from "ui/settings/SettingsMenu";
import SettingsSheetNodeOther from "ui/settings/SettingsSheetNodeOther";
import SettingsSheetClientOther from "ui/settings/SettingsSheetClientOther";
import "./SettingsPage.css";

const SettingsPage = ({tab}) => (
    <Page>
        <h2>Settings</h2>
        <br/>

        <SettingsTabs />
        <div className="row settings-notebook">
            <SettingsMenu />
            <div className="col-md-10">
                {tab === "node" && <SettingsSheetNodeOther/>}
                {tab === "client" && <SettingsSheetClientOther/>}
            </div>
        </div>
    </Page>
);

export default connect(
    state => ({
        tab: state.settings.tab
    })
)(SettingsPage);
