export function isPermitted(operation, operations, permissions, state) {
    const requirements = operations[operation];
    if (!requirements) {
        return false;
    }
    const isOwner = state.home.root.api === state.node.root.api;
    for (let r of requirements) {
        switch (r) {
            case "owner":
                if (isOwner) {
                    return true;
                }
                break;
            case "admin":
                if (isOwner && permissions.includes(r)) {
                    return true;
                }
                break;
            default:
                if (permissions.includes(r)) {
                    return true;
                }
        }
    }
    return false;
}
